import Vue from 'vue';
import Vuex from 'vuex';
import { uuid } from "vue-uuid";
import constant from "./constants.js"
import { saveAs } from 'file-saver'; // eslint-disable-line no-unused-vars


Vue.use(Vuex);

export default new Vuex.Store({
    state: {
        openProjectIndex: 0,
        navigatorList: [],
        SAHLProject: [],
        activeUUID: null,
        detailViewUUID: null,
        connectionLine: [],
        ismakeProject: false,
        detailViewerList: [], // project가 바뀔때 마다 reset해야한다.
        idexDetailView: undefined,
        detailViewer: { uuid: '', element: '' }
    },
    getters: {
        projectCount(state) {
            return state.SAHLProject.length;
        },
        getImplementationDataType(state) {
            var datatype = state.SAHLProject[state.openProjectIndex].DataTypes.ImplementationDataType.map(ele => {
                var returnObj = {}
                returnObj['name'] = ele.name
                returnObj['uuid'] = ele.uuid
                return returnObj
            })
            return datatype
        },
        getCompuMethod(state) {
            var datatype = state.SAHLProject[state.openProjectIndex].DataTypes.CompuMethod.map(ele => {
                var returnObj = {}
                returnObj['name'] = ele.name
                returnObj['uuid'] = ele.uuid
                return returnObj
            })
            return datatype
        },
        getDataConstr(state) {
            var datatype = state.SAHLProject[state.openProjectIndex].DataTypes.DataConstr.map(ele => {
                var returnObj = {}
                returnObj['name'] = ele.name
                returnObj['uuid'] = ele.uuid
                return returnObj
            })
            return datatype
        },
        getMachineDesign(state) {
            var datatype = state.SAHLProject[state.openProjectIndex].Machine.MachineDesign.map(ele => {
                var returnObj = {}
                returnObj['name'] = ele.name
                returnObj['uuid'] = ele.uuid
                return returnObj
            })
            return datatype
        },
        getHWElement(state) {
            var datatype = state.SAHLProject[state.openProjectIndex].Machine.HWElement.map(ele => {
                var returnObj = {}
                returnObj['name'] = ele.name
                returnObj['uuid'] = ele.uuid
                return returnObj
            })
            return datatype
        },
        getModeDeclaration(state) {
            var datatype = state.SAHLProject[state.openProjectIndex].Machine.ModeDeclarationGroup.map(ele => {
                var returnObj = {}
                returnObj['name'] = ele.name
                returnObj['uuid'] = ele.uuid
                return returnObj
            })
            return datatype
        },
        getHWCategory(state) {
            var datatype = state.SAHLProject[state.openProjectIndex].Machine.HWCategoty.map(ele => {
                var returnObj = {}
                returnObj['name'] = ele.name
                returnObj['uuid'] = ele.uuid
                return returnObj
            })
            return datatype
        },
        getHWAttribute(state) {
            var datatype = state.SAHLProject[state.openProjectIndex].Machine.HWCategoty.map(ele => {
                var returnObj = {}
                returnObj['name'] = ele.name
                returnObj['uuid'] = ele.uuid
                return returnObj
            })
            return datatype
        },
        getNetworkEndPoint(state) {
            var datatype = []
            for (let i = 0; i < state.SAHLProject[state.openProjectIndex].Machine.EthernetCluster.length; i++) {
                for (let n = 0; n < state.SAHLProject[state.openProjectIndex].Machine.EthernetCluster[i].conditional.length; n++) {
                    for (let v = 0; v < state.SAHLProject[state.openProjectIndex].Machine.EthernetCluster[i].conditional[n].channel.length; v++) {
                        for (let c = 0; c < state.SAHLProject[state.openProjectIndex].Machine.EthernetCluster[i].conditional[n].channel[v].endpoint.length; c++) {
                            var ecname = state.SAHLProject[state.openProjectIndex].Machine.EthernetCluster[i].name
                            var condiname = state.SAHLProject[state.openProjectIndex].Machine.EthernetCluster[i].conditional[n].name
                            var channelname = state.SAHLProject[state.openProjectIndex].Machine.EthernetCluster[i].conditional[n].channel[v].name
                            var name = state.SAHLProject[state.openProjectIndex].Machine.EthernetCluster[i].conditional[n].channel[v].endpoint[c].name
                            datatype.push({
                                uuid: state.SAHLProject[state.openProjectIndex].Machine.EthernetCluster[i].uuid,
                                ethernetname: state.SAHLProject[state.openProjectIndex].Machine.EthernetCluster[i].name,
                                condidx: n,
                                channelidx: v,
                                endpoineidx: c,
                                fullname: ecname + '/' + condiname + '/' + channelname + '/' + name
                            })
                        }
                    }
                }
            }
            return datatype
        },
        getCommunicationConnector(state) {
            var datatype = []
            for (let i = 0; i < state.SAHLProject[state.openProjectIndex].Machine.MachineDesign.length; i++) {
                for (let n = 0; n < state.SAHLProject[state.openProjectIndex].Machine.MachineDesign[i].connector.length; n++) {
                    datatype.push({
                        name: state.SAHLProject[state.openProjectIndex].Machine.MachineDesign[i].connector[n].name,
                        mtu: state.SAHLProject[state.openProjectIndex].Machine.MachineDesign[i].connector[n].mtu,
                        mtuenable: state.SAHLProject[state.openProjectIndex].Machine.MachineDesign[i].connector[n].nmtuenableme,
                        une: state.SAHLProject[state.openProjectIndex].Machine.MachineDesign[i].connector[n].une,
                        endpoint: state.SAHLProject[state.openProjectIndex].Machine.MachineDesign[i].connector[n].endpoint,
                        uuid: state.SAHLProject[state.openProjectIndex].Machine.MachineDesign[i].uuid,
                        mdname: state.SAHLProject[state.openProjectIndex].Machine.MachineDesign[i].name,
                    })
                }
            }
            return datatype
        },
        gettreeviewitems(state) {
            const replaceChildren = (obj, parent) => {
                const clone = Object.assign({}, obj)
                delete clone.children
                if (parent) clone.parent = parent
                return clone
            }

            const addItems = (arr, parent) => {
                const items = arr.reduce((acc, x) => {
                    acc.push(replaceChildren(x, parent))
                    if (x.children) {
                        acc.push(addItems(x.children, x.uuid))
                    }
                    return acc
                }, [])

                return items.flat()
            }

            return addItems(state.navigatorList[state.openProjectIndex].children).reduce((acc, x) => {
                acc[x.uuid] = x
                return acc
            }, {})
        },
        getconnectLineNum: (state) => (startID) => {
            return state.connectionLine[state.openProjectIndex].start.findIndex(id => id == startID)
        },
        getChangeEndLine: (state, getters) => (startID) => {
            var idxstartId = getters.getconnectLineNum(startID)
                //console.log(idxstartId + '+' + state.connectionLine[state.openProjectIndex].end[idxstartId])
            return state.connectionLine[state.openProjectIndex].end[idxstartId]
        },
        getDataImplementation: (state) => (uuid) => {
            return state.SAHLProject[state.openProjectIndex].DataTypes.ImplementationDataType.find(data => data.uuid === uuid)
        },
        getDataCompuMethod: (state) => (uuid) => {
            return state.SAHLProject[state.openProjectIndex].DataTypes.CompuMethod.find(data => data.uuid === uuid)
        },
        getDataDataConstr: (state) => (uuid) => {
            return state.SAHLProject[state.openProjectIndex].DataTypes.DataConstr.find(data => data.uuid === uuid)
        },
        getDataMachineDesign: (state) => (uuid) => {
            return state.SAHLProject[state.openProjectIndex].Machine.MachineDesign.find(data => data.uuid === uuid)
        },
        getDataEthernetCluster: (state) => (uuid) => {
            return state.SAHLProject[state.openProjectIndex].Machine.EthernetCluster.find(data => data.uuid === uuid)
        },
        getDetailViewlength(state) {
            return state.detailViewerList.length - 1
        },
        getchangenamelist: (state) => (uuid) => {
            var idx = state.connectionLine[state.openProjectIndex].end.indexOf(uuid)
            var indices = []
            while (idx != -1) {
                indices.push(idx)
                idx = state.connectionLine[state.openProjectIndex].end.indexOf(uuid, idx + 1)
            }
            return indices
        },
        getImplementationName: (state) => (uuid) => { // service Interface->method->in/out arg edit dialog일때 type수정했을 경우 type이름 수정해 주려고 넣었음 
            var idx = state.SAHLProject[state.openProjectIndex].DataTypes.ImplementationDataType.findIndex(data => data.uuid === uuid)
            return state.SAHLProject[state.openProjectIndex].DataTypes.ImplementationDataType[idx].name
        },
        getactiveLine: (state) => (uuid) => {
            var indices = []
            for (let i = 0; i < state.connectionLine[state.openProjectIndex].start.length; i++) {
                var onlyuuid = state.connectionLine[state.openProjectIndex].start[i].split('/')
                if (onlyuuid[0] == uuid) {
                    indices.push(i)
                }
            }
            console.log('+getactiveLine++++++' + uuid + '///' + indices[0])

            return indices
        },
        getSaveObject: (state) => (savelist) => {
            var saveObject = []
            savelist.forEach(data => {
                if (data.parent == constant.CompuMethod_str) {
                    var idxCompu = state.SAHLProject[state.openProjectIndex].DataTypes.CompuMethod.findIndex(item => item.uuid === data.uuid)
                    saveObject.push(state.SAHLProject[state.openProjectIndex].DataTypes.CompuMethod[idxCompu])
                } else if (data.parent == constant.DataConstr_str) {
                    var idxDdatacon = state.SAHLProject[state.openProjectIndex].DataTypes.DataConstr.findIndex(item => item.uuid === data.uuid)
                    saveObject.push(state.SAHLProject[state.openProjectIndex].DataTypes.DataConstr[idxDdatacon])
                } else if (data.parent == constant.ApplicationArray_str) {
                    var idxAappli = state.SAHLProject[state.openProjectIndex].DataTypes.ApplicationArrayDataType.findIndex(item => item.uuid === data.uuid)
                    saveObject.push(state.SAHLProject[state.openProjectIndex].DataTypes.ApplicationArrayDataType[idxAappli])
                } else if (data.parent == constant.Implementation_str) {
                    var idxImple = state.SAHLProject[state.openProjectIndex].DataTypes.ImplementationDataType.findIndex(item => item.uuid === data.uuid)
                    saveObject.push(state.SAHLProject[state.openProjectIndex].DataTypes.ImplementationDataType[idxImple])
                } else if (data.parent == constant.ServiceInterface_str) {
                    var idxService = state.SAHLProject[state.openProjectIndex].Service.ServiceInterface.findIndex(item => item.uuid === data.uuid)
                    saveObject.push(state.SAHLProject[state.openProjectIndex].Service.ServiceInterface[idxService])
                } else if (data.parent == constant.Machine_str) {
                    var idxMachine = state.SAHLProject[state.openProjectIndex].Machine.Machine.findIndex(item => item.uuid === data.uuid)
                    saveObject.push(state.SAHLProject[state.openProjectIndex].Machine.Machine[idxMachine])
                } else if (data.parent == constant.MachineDesigne_str) {
                    var idxMachineD = state.SAHLProject[state.openProjectIndex].Machine.MachineDesign.findIndex(item => item.uuid === data.uuid)
                    saveObject.push(state.SAHLProject[state.openProjectIndex].Machine.MachineDesign[idxMachineD])
                } else if (data.parent == constant.EthernetCluster_str) {
                    var idxEthernet = state.SAHLProject[state.openProjectIndex].Machine.EthernetCluster.findIndex(item => item.uuid === data.uuid)
                    saveObject.push(state.SAHLProject[state.openProjectIndex].Machine.EthernetCluster[idxEthernet])
                } else if (data.parent == constant.ModeDeclarationGroup_str) {
                    var idxModeD = state.SAHLProject[state.openProjectIndex].Machine.ModeDeclarationGroup.findIndex(item => item.uuid === data.uuid)
                    saveObject.push(state.SAHLProject[state.openProjectIndex].Machine.ModeDeclarationGroup[idxModeD])
                } else if (data.parent == constant.HWElement_str) {
                    var idxHWE = state.SAHLProject[state.openProjectIndex].Machine.HWElement.findIndex(item => item.uuid === data.uuid)
                    saveObject.push(state.SAHLProject[state.openProjectIndex].Machine.HWElement[idxHWE])
                } else if (data.parent == constant.HWCategoty_str) {
                    var idxHWC = state.SAHLProject[state.openProjectIndex].Machine.HWCategory.findIndex(item => item.uuid === data.uuid)
                    saveObject.push(state.SAHLProject[state.openProjectIndex].Machine.HWCategory[idxHWC])
                }
            })
            return saveObject
        }
    },
    mutations: {
        saveOpenProject(state, payload) {
            state = payload
        },
        selectOpenProject(state, payload) {
            state.openProjectIndex = payload.openProjectIndex
        },
        setmakeProject(state, payload) {
            state.ismakeProject = payload.makeproject
        },
        addProject(state, payload) {
            var newUUid = uuid.v1()
            state.SAHLProject.push({
                uuid: newUUid,
                name: payload.name,
                DataTypes: { CompuMethod: [], DataConstr: [], ApplicationArrayDataType: [], ImplementationDataType: [] },
                Service: {
                    ServiceInterface: [],
                    SomeIPServiceInterfaceDeployment: [],
                    SomeIPServerEvent: [],
                    SomeIPClientEvent: [],
                    ErrorDomain: [],
                    Error: [],
                    ErrorSet: [],
                    SomeIPClientServiceInstance: [],
                    SomeIPServerServiceInstance: [],
                    SomeIPServiceInstanceToMachine: [],
                    ServiceInstanceToPortPrototype: [],
                    RequiredSomeIP: [],
                    ProvidedSomeIP: []
                },
                Machine: { Machine: [], MachineDesign: [], EthernetCluster: [], ModeDeclarationGroup: [], HWElement: [], HWCategoty: [] },
                AdaptiveApplication: { ProtoMachineMapping: [], SWComponents: [], Process: [], Executable: [], StartupConfig: [], DeterministicClient: [], ProcessDesign: [] },
                Platform: {}
            })
            state.navigatorList.push({
                uuid: newUUid,
                name: payload.name,
                children: [{
                        uuid: constant.DateType_str,
                        name: constant.DateType_str,
                        icon: "mdi-folder",
                        children: [
                            { uuid: constant.CompuMethod_str, name: constant.CompuMethod_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                            { uuid: constant.DataConstr_str, name: constant.DataConstr_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                            { uuid: constant.ApplicationArray_str, name: constant.ApplicationArray_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                            { uuid: constant.Implementation_str, name: constant.Implementation_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                        ]
                    },
                    {
                        uuid: constant.Service_str,
                        name: constant.Service_str,
                        icon: "mdi-folder",
                        children: [{
                                uuid: constant.ServiceInterfaces_str,
                                name: constant.ServiceInterfaces_str,
                                icon: "mdi-folder",
                                children: [{ uuid: constant.SomeIPServiceInterfaceDeployment_str, name: constant.SomeIPServiceInterfaceDeployment_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                                    { uuid: constant.ServiceInterface_str, name: constant.ServiceInterface_str, icon: 'mdi-alpha-e-circle-outline', children: [] }
                                ]
                            },
                            {
                                uuid: constant.SomeIPEvents_str,
                                name: constant.SomeIPEvents_str,
                                icon: "mdi-folder",
                                children: [{ uuid: constant.Client_str, name: constant.Client_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                                    { uuid: constant.Server_str, name: constant.Server_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                                ]
                            },
                            {
                                uuid: constant.ServiceInstances_str,
                                name: constant.ServiceInstances_str,
                                icon: "mdi-folder",
                                children: [{ uuid: constant.SomeIPClient_str, name: constant.SomeIPClient_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                                    { uuid: constant.SomeIPServer_str, name: constant.SomeIPServer_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                                    { uuid: constant.SomeIPToMachineMapping_str, name: constant.SomeIPToMachineMapping_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                                    { uuid: constant.ToPortPrototypeMapping_str, name: constant.ToPortPrototypeMapping_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                                    { uuid: constant.RequiredSomeIP_str, name: constant.RequiredSomeIP_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                                    { uuid: constant.ProvidedSomeIP_str, name: constant.ProvidedSomeIP_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                                ]
                            },
                            {
                                uuid: constant.Errors_str,
                                name: constant.Errors_str,
                                icon: "mdi-folder",
                                children: [{ uuid: constant.Error_str, name: constant.Error_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                                    { uuid: constant.Errorset_str, name: constant.Errorset_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                                    { uuid: constant.ErrorDomain_str, name: constant.ErrorDomain_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                                ]
                            },
                        ]
                    },
                    {
                        uuid: constant.AdaptiveApplication_str,
                        name: constant.AdaptiveApplication_str,
                        icon: "mdi-folder",
                        children: [
                            { uuid: constant.ProcesstoMachineMapping_str, name: constant.ProcesstoMachineMapping_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                            { uuid: constant.SWComponents_str, name: constant.SWComponents_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                            { uuid: constant.Process_str, name: constant.Process_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                            { uuid: constant.ProcessDesign_str, name: constant.ProcessDesign_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                            { uuid: constant.Executable_str, name: constant.Executable_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                            { uuid: constant.StartupConfig_str, name: constant.StartupConfig_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                            { uuid: constant.DeterministicClient_str, name: constant.DeterministicClient_str, icon: 'mdi-alpha-e-circle-outline', children: [] }
                        ]
                    },
                    {
                        uuid: constant.Machines_str,
                        name: constant.Machines_str,
                        icon: "mdi-folder",
                        children: [
                            { uuid: constant.Machine_str, name: constant.Machine_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                            { uuid: constant.MachineDesigne_str, name: constant.MachineDesigne_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                            { uuid: constant.EthernetCluster_str, name: constant.EthernetCluster_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                            { uuid: constant.ModeDeclarationGroup_str, name: constant.ModeDeclarationGroup_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                            { uuid: constant.HWElement_str, name: constant.HWElement_str, icon: 'mdi-alpha-e-circle-outline', children: [] },
                            { uuid: constant.HWCategoty_str, name: constant.HWCategoty_str, icon: 'mdi-alpha-e-circle-outline', children: [] }
                        ]
                    },
                    {
                        uuid: constant.Platform_str,
                        name: constant.Platform_str,
                        icon: "mdi-folder",
                        children: []
                    }
                ]
            })
            state.connectionLine.push({ start: [], end: [] })
        },
        setuuid(state, payload) {
            state.activeUUID = payload.uuid
        },
        setConnectionline(state, payload) {
            //console.log('setConnectionline ' + payload.start + ' /// ' + payload.end)
            state.connectionLine[state.openProjectIndex].start.push(payload.start)
            state.connectionLine[state.openProjectIndex].end.push(payload.end)
        },
        deletConnectionline(state, payload) {
            state.connectionLine[state.openProjectIndex].start.splice(payload.startnum, 1)
            state.connectionLine[state.openProjectIndex].end.splice(payload.startnum, 1)
        },
        setDetailView(state, payload) {
            console.log('setDetailView**' + state.detailViewer.uuid + '//' + payload.uuid)
            if (state.detailViewer.uuid != payload.uuid) {
                console.log('...' + state.idexDetailView + '+' + state.detailViewerList.length)
                if (state.idexDetailView < state.detailViewerList.length - 1) {
                    console.log('.##.' + state.detailViewerList[state.idexDetailView + 1].uuid + '+' + payload.uuid)
                    if (state.detailViewerList[state.idexDetailView + 1].uuid != payload.uuid) {
                        state.detailViewerList.splice(state.idexDetailView + 1)
                        console.log('splice' + state.idexDetailView)
                    } else {
                        this.commit('setfrontDetailView')
                        console.log('splice else ' + state.idexDetailView)
                        return
                    }
                }
                state.detailViewer = { uuid: payload.uuid, element: payload.element }
                state.detailViewerList.push({ uuid: payload.uuid, element: payload.element })
                state.idexDetailView = state.detailViewerList.length - 1
                state.detailViewUUID = payload.uuid
                console.log('set//' + state.idexDetailView + '//' + state.detailViewer.element + '//' + state.detailViewer.uuid)
                console.log(state.detailViewerList)
            } else {
                state.detailViewerList.splice(state.detailViewerList.length - 1, 1)
                state.detailViewer = { uuid: payload.uuid, element: payload.element }
                state.detailViewerList.push({ uuid: payload.uuid, element: payload.element })
                state.idexDetailView = state.detailViewerList.length - 1
                state.detailViewUUID = payload.uuid
            }
        },
        setBackDetailView(state) {
            if (state.idexDetailView == 0) {
                return
            }
            state.idexDetailView -= 1
            state.detailViewer = { uuid: state.detailViewerList[state.idexDetailView].uuid, element: state.detailViewerList[state.idexDetailView].element }
            state.detailViewUUID = state.detailViewerList[state.idexDetailView].uuid
            console.log('back' + state.detailViewerList[state.idexDetailView].element + '//' + state.detailViewer.uuid)
        },
        setfrontDetailView(state) {
            if (state.idexDetailView == state.detailViewerList.length - 1) {
                return
            }
            state.idexDetailView += 1
            state.detailViewer = { uuid: state.detailViewerList[state.idexDetailView].uuid, element: state.detailViewerList[state.idexDetailView].element }
            state.detailViewUUID = state.detailViewerList[state.idexDetailView].uuid
            console.log('front' + state.detailViewer.element + '//' + state.detailViewer.uuid)

        },

        addElementService(state, payload) {
            var newUUid = uuid.v1()
            state.SAHLProject[state.openProjectIndex].Service.ServiceInterface.push({
                uuid: newUUid,
                top: payload.top,
                left: payload.left,
                zindex: payload.zindex,
                name: payload.name,
                version: payload.version,
                namespace: payload.namespace,
                isservice: false,
                events: payload.events,
                fields: payload.fields,
                methods: payload.methods
            })
            state.navigatorList[state.openProjectIndex].children[constant.Service_index].children[constant.ServiceInterfaces_index].children[constant.ServiceInterface_index].children.push({ uuid: newUUid, name: payload.name, icon: payload.icon })
            state.activeUUID = newUUid
        },
        editServiceInterface(state, payload) {
            var idxElement = state.SAHLProject[state.openProjectIndex].Service.ServiceInterface.findIndex(data => data.uuid === payload.uuid)

            if (payload.compo == "Name") {
                state.navigatorList[state.openProjectIndex].children[constant.Service_index].children[constant.ServiceInterfaces_index].children[constant.ServiceInterface_index].children[idxElement].name = payload.name
            } else if (payload.compo == "Event") {
                state.SAHLProject[state.openProjectIndex].Service.ServiceInterface[idxElement].events = payload.events.slice()
            } else if (payload.compo == "Field") {
                state.SAHLProject[state.openProjectIndex].Service.ServiceInterface[idxElement].fields = payload.fields.slice()
            } else if (payload.compo == "Method") {
                state.SAHLProject[state.openProjectIndex].Service.ServiceInterface[idxElement].methods = payload.methods.slice()
            } else if (payload.compo == "drag") {
                state.SAHLProject[state.openProjectIndex].Service.ServiceInterface[idxElement].top = payload.top
                state.SAHLProject[state.openProjectIndex].Service.ServiceInterface[idxElement].left = payload.left
            } else if (payload.compo == "z") {
                state.SAHLProject[state.openProjectIndex].Service.ServiceInterface[idxElement].zindex = payload.zindex
            }
        },
        addElementCompuMehtod(state, payload) {
            var newUUid = uuid.v1()
            state.SAHLProject[state.openProjectIndex].DataTypes.CompuMethod.push({
                uuid: newUUid,
                top: payload.top,
                left: payload.left,
                zindex: payload.zindex,
                name: payload.name,
                category: payload.category,
                scales: payload.namesscalespace,
            })
            state.navigatorList[state.openProjectIndex].children[constant.DateType_index].children[constant.CompuMethod_index].children.push({ uuid: newUUid, name: payload.name, icon: payload.icon })
            state.activeUUID = newUUid
        },
        editCompuMehtod(state, payload) {
            var idxElement = state.SAHLProject[state.openProjectIndex].DataTypes.CompuMethod.findIndex(data => data.uuid === payload.uuid)

            if (payload.compo == "Name") {
                state.navigatorList[state.openProjectIndex].children[constant.DateType_index].children[constant.CompuMethod_index].children[idxElement].name = payload.name
